mapper.insert:

    SELECT LAST_INSERT_ID()

INSERT INTO TEAM (
      LEADER_ID
    , TEAM_NAME
    , HOMETOWN
    , INTRODUCTION
    , FOUNDATION_YMD
    , REG_DATE
    , UPDATE_DATE
    , SIDO_CODE
    , SIGUNGU_CODE
) VALUES (
      #{leaderId}
    , #{teamName}
    , #{hometown}
    , #{introduction}
    , #{foundationYmd}
    , #{regDate}
    , #{updateDate}
    , #{sidoCode}
    , #{sigunguCode}
)

mapper.update:
UPDATE TEAM
   SET LEADER_ID = #{leaderId}
     , TEAM_IMAGE_PATH = #{teamImagePath}
     , TEAM_NAME = #{teamName}
     , HOMETOWN = #{hometown}
     , INTRODUCTION = #{introduction}
     , FOUNDATION_YMD = #{foundationYmd}
     , UPDATE_DATE = #{updateDate}
     , SIDO_CODE = #{sidoCode}
     , SIGUNGU_CODE = #{sigunguCode}
 WHERE TEAM_SEQ = #{teamSeq}

mapper.select:
SELECT * FROM ( SELECT COUNT(*) OVER() TOTAL_COUNT , A.TEAM_SEQ , A.LEADER_ID , A.TEAM_NAME , A.TEAM_IMAGE_PATH , A.HOMETOWN , A.INTRODUCTION , A.FOUNDATION_YMD , A.REG_DATE , A.UPDATE_DATE , A.SIDO_CODE , A.SIGUNGU_CODE , IFNULL(B.TOT_MEMBER, 0) TOT_MEMBER FROM TEAM A LEFT JOIN ( SELECT TEAM_SEQ , COUNT(*) TOT_MEMBER FROM TEAM_MEMBER WHERE WITHDRAWAL_YN != 'Y' GROUP BY TEAM_SEQ ) B ON A.TEAM_SEQ = B.TEAM_SEQ LEFT JOIN ( SELECT TEAM_SEQ , DAY_OF_THE_WEEK_CODE , START_TIME , END_TIME FROM TEAM_REGULAR_EXERCISE ) C ON A.TEAM_SEQ = C.TEAM_SEQ
AND A.TEAM_NAME LIKE CONCAT('%', IFNULL(#{teamName}, ''), '%')
AND A.SIGUNGU_CODE = #{sigungu}
AND DAY_OF_THE_WEEK_CODE >= #{startDay} AND DAY_OF_THE_WEEK_CODE <= #{endDay}
AND START_TIME >= #{startTime} AND END_TIME <= #{endTime}
GROUP BY A.TEAM_SEQ ORDER BY A.TEAM_NAME ) ORDERS LIMIT #{pagerDTO.offset} OFFSET #{pagerDTO.pageNo}
