mapper.insert:
INSERT INTO
    TEAM (     LEADER_ID
             , TEAM_NAME
             , HOMETOWN
             , INTRODUCTION
             , FOUNDATION_YMD
             , REG_DATE
             , UPDATE_DATE
             , SIDO_CODE
             , SIGUNGU_CODE )
VALUES (      #{leaderId}
            , #{teamName}
            , #{hometown}
            , #{introduction}
            , #{foundationYmd}
            , #{regDate}
            , #{updateDate}
            , #{sidoCode}
            , #{sigunguCode} )

mapper.update:
UPDATE TEAM
   SET LEADER_ID = #{leaderId}
     , TEAM_IMAGE_PATH = #{teamImagePath}
     , TEAM_NAME = #{teamName}
     , HOMETOWN = #{hometown}
     , INTRODUCTION = #{introduction}
     , FOUNDATION_YMD = #{foundationYmd}
     , UPDATE_DATE = #{updateDate}
     , SIDO_CODE = #{sidoCode}
     , SIGUNGU_CODE = #{sigunguCode}
 WHERE TEAM_SEQ = #{teamSeq}

mapper.select:
SELECT * FROM ( SELECT DISTINCT A.TEAM_SEQ , A.LEADER_ID , A.TEAM_NAME , A.TEAM_IMAGE_PATH , A.HOMETOWN , A.INTRODUCTION , A.FOUNDATION_YMD , A.REG_DATE , A.UPDATE_DATE , A.SIDO_CODE , A.SIGUNGU_CODE , COUNT(B.USER_SEQ) OVER(PARTITION BY B.TEAM_SEQ) AS TOT_MEMBER FROM TEAM A LEFT JOIN (SELECT * FROM TEAM_MEMBER WHERE WITHDRAWAL_YN != 'Y') B ON A.TEAM_SEQ = B.TEAM_SEQ WHERE 1=1
AND A.TEAM_NAME LIKE CONCAT('%', IFNULL(#{teamName}, ''), '%')
AND A.SIGUNGU_CODE = #{sigungu}
ORDER BY A.TEAM_NAME ) ORDERS LIMIT #{pagerDTO.offset} OFFSET #{pagerDTO.pageNo}
