mapper.select:
SELECT TEAM_REGULAR_EXERCISE_SEQ
     , TEAM_SEQ
     , DAY_OF_THE_WEEK_CODE
     , START_TIME
     , END_TIME
     , EXERCISE_PLACE_ADDRESS
     , EXERCISE_PLACE_NAME
  FROM TEAM_REGULAR_EXERCISE
 WHERE TEAM_SEQ = #{teamSeq}

   AND A.START_DATE = #{startDate}


   AND A.END_DATE = #{endDate}


   AND A.START_TIME = #{startTime}


   AND A.END_TIME = #{endTime}

mapper.insert:
INSERT
INTO TEAM_REGULAR_EXERCISE ( TEAM_SEQ
                           , DAY_OF_THE_WEEK_CODE
                           , START_TIME
                           , END_TIME
                           , EXERCISE_PLACE_ADDRESS
                           , EXERCISE_PLACE_NAME)
VALUES ( #{teamSeq}
       , #{dayOfTheWeekCode}
       , #{startTime}
       , #{endTime}
       , #{exercisePlaceAddress}
       , #{exercisePlaceName})

mapper.update:
UPDATE TEAM_REGULAR_EXERCISE
SET TEAM_SEQ               = #{teamSeq}
  , DAY_OF_THE_WEEK_CODE   = #{dayOfTheWeekCode}
  , START_TIME             = #{startTime}
  , END_TIME               = #{endTime}
  , EXERCISE_PLACE_ADDRESS = #{exercisePlaceAddress}
  , EXERCISE_PLACE_NAME    = #{exercisePlaceName}
WHERE TEAM_REGULAR_EXERCISE_SEQ = #{teamRegularExerciseSeq}
