mapper.insert:
INSERT INTO USER ( USER_ID
                 , PWD
                 , NAME
                 , EMAIL
                 , REG_DATE
                 , UPDATE_DATE
                 , USER_REG_YN
                 , GENDER
                 , HEIGHT
                 , WEIGHT
                 , SIDO_CODE
                 , SIGUNGU_CODE
                 , POSITION_CODE)
VALUES ( #{userId}
       , #{password}
       , #{userName}
       , #{email}
       , #{regDate}
       , #{updateDate}
       , #{userRegYn}
       , #{gender}
       , #{height}
       , #{weight}
       , #{sidoCode}
       , #{sigunguCode}
       , #{positionCode})

mapper.select:
SELECT USER_SEQ
FROM USER
WHERE USER_ID = #{userId}

mapper.update:
UPDATE USER
SET NAME=#{userName}
  , EMAIL=#{email}
  , GENDER=#{gender}
  , HEIGHT=#{height}
  , WEIGHT=#{weight}
  , SIDO_CODE=#{sidoCode}
  , SIGUNGU_CODE=#{sigunguCode}
  , POSITION_CODE=#{positionCode}
  , ROADADDRESS=#{roadAddress}
WHERE USER_SEQ=#{userSeq}

UPDATE USER
SET PWD=#{curPassword}
WHERE USER_SEQ=#{userSeq}
