mapper.insert:

    SELECT LAST_INSERT_ID()

/* GameJoinPlayerRepository.save */
INSERT INTO GAME_JOIN_PLAYER (
  GAME_JOIN_TEAM_SEQ
, PLAYER_TYPE_CODE
, USER_SEQ
, NAME
, BACK_NUMBER
, POSITION_CODE
, EMAIL
) VALUES (
  #{gameJoinTeamSeq}
, #{playerTypeCode}
, #{userSeq}
, #{name}
, #{backNumber}
, #{positionCode}
, #{email}
)

mapper.delete:
/* GameJoinPlayerRepository.deletePlayers */
DELETE
  FROM GAME_JOIN_PLAYER
 WHERE 1=1
   AND GAME_JOIN_TEAM_SEQ = #{gameJoinTeamSeq}
