mapper.insert:

    SELECT LAST_INSERT_ID()

/* GameJoinPlayerRepository.save */
INSERT INTO GAME_JOIN_PLAYER (
  GAME_JOIN_TEAM_SEQ
, GAME_SEQ
, HOME_AWAY_CODE
, PLAYER_TYPE_CODE
, USER_SEQ
, NAME
, BACK_NUMBER
, POSITION_CODE
, EMAIL
) VALUES (
  #{gameJoinTeamSeq}
, #{gameSeq}
, #{homeAwayCode}
, #{playerTypeCode}
, #{userSeq}
, #{name}
, #{backNumber}
, #{positionCode}
, #{email}
)

mapper.select:
/* GameJoinPlayerRepository.findPlayers */
SELECT

  FROM GAME_JOIN_PLAYER
 WHERE 1=1


   AND GAME_JOIN_TEAM_SEQ = #{gameJoinTeamSeq}


   AND GAME_SEQ = #{gameSeq}
   AND HOME_AWAY_CODE = #{homeAwayCode}

mapper.delete:
/* GameJoinPlayerRepository.deletePlayers */
DELETE
  FROM GAME_JOIN_PLAYER
 WHERE 1=1


   AND GAME_JOIN_TEAM_SEQ = #{gameJoinTeamSeq}


   AND GAME_SEQ = #{gameSeq}
   AND HOME_AWAY_CODE = #{homeAwayCode}
