mapper.insert:

    SELECT LAST_INSERT_ID()

/* GameRepository.saveGame */
INSERT INTO
  GAME (
        CREATOR_TEAM_MEMBER_SEQ
      , GAME_RECORD_STATE_CODE
      , GAME_TYPE_CODE
      , GAME_YMD
      , GAME_START_TIME
      , GAME_END_TIME
      , GAME_PLACE_ADDRESS
      , GAME_PLACE_NAME
      , REG_DATE
      , SIDO_CODE
      , SIGUNGU_CODE
      , QUARTER_TIME1
      , QUARTER_TIME2
      , QUARTER_TIME3
      , QUARTER_TIME4
)
VALUES (
        #{creatorTeamMemberSeq}
      , #{gameRecordStateCode}
      , #{gameTypeCode}
      , #{gameYmd}
      , #{gameStartTime}
      , #{gameEndTime}
      , #{gamePlaceAddress}
      , #{gamePlaceName}
      , #{regDate}
      , #{sidoCode}
      , #{sigunguCode}
      , #{quarterTime1}
      , #{quarterTime2}
      , #{quarterTime3}
      , #{quarterTime4}
)

mapper.delete:
DELETE FROM GAME
WHERE 1=1
AND GAME_SEQ = #{gameSeq}

mapper.update:
UPDATE GAME
   SET GAME_RECORD_STATE_CODE = #{gameRecordStateCode}
 WHERE GAME_SEQ = #{gameSeq}

UPDATE GAME
   SET

       QUARTER_TIME1 = #{quarterTime1}


       QUARTER_TIME2 = #{quarterTime2}


       QUARTER_TIME3 = #{quarterTime3}


       QUARTER_TIME4 = #{quarterTime4}

WHERE GAME_SEQ = #{gameSeq}
