mapper.select:
SELECT QUARTER_PLAYER_RECORDS_SEQ , GAME_SEQ , GAME_JOIN_PLAYER_SEQ , GAME_JOIN_TEAM_SEQ , QUARTER_CODE , IN_GAME_YN , TRY_FREE_THROW , FREE_THROW , TRY_TWO_POINT , TWO_POINT , TRY_THREE_POINT , THREE_POINT , REBOUND , STEAL , BLOCK , TURNOVER , FOUL FROM QUARTER_PLAYER_RECORDS
QUARTER_PLAYER_RECORDS_SEQ = #{quarterPlayerRecordsSeq}
GAME_SEQ = #{gameSeq} AND GAME_JOIN_PLAYER_SEQ = #{gameJoinPlayerSeq} AND GAME_JOIN_TEAM_SEQ = #{gameJoinTeamSeq} AND QUARTER_CODE = #{quarterCode}

mapper.update:
UPDATE QUARTER_PLAYER_RECORDS
   SET QUARTER_CODE = #{quarterCode}
     , IN_GAME_YN = #{inGameYn}
     , TRY_FREE_THROW = #{tryFreeThrow}
     , FREE_THROW = #{freeThrow}
     , TRY_TWO_POINT = #{tryTwoPoint}
     , TWO_POINT = #{twoPoint}
     , TRY_THREE_POINT = #{tryThreePoint}
     , THREE_POINT = #{threePoint}
     , REBOUND = #{rebound}
     , STEAL = #{steal}
     , BLOCK = #{block}
     , TURNOVER = #{turnover}
     , FOUL = #{foul}



       QUARTER_PLAYER_RECORDS_SEQ = #{quarterPlayerRecordsSeq}


       GAME_SEQ = #{gameSeq}
   AND GAME_JOIN_PLAYER_SEQ = #{gameJoinPlayerSeq}
   AND GAME_JOIN_TEAM_SEQ = #{gameJoinTeamSeq}
   AND QUARTER_CODE = #{quarterCode}

/* QuarterPlayerRecordsRepository.modifyInGameYn */
UPDATE QUARTER_PLAYER_RECORDS
   SET IN_GAME_YN = #{inGameYn}
 WHERE 1=1
   AND QUARTER_CODE         = #{quarterCode}


   AND GAME_JOIN_PLAYER_SEQ = #{gameJoinPlayerSeq}


   AND GAME_JOIN_TEAM_SEQ   = #{gameJoinTeamSeq}
