mapper.select:
/* QuarterTeamRecordsRepository.findAllGameJoinTeam */
SELECT

  FROM QUARTER_TEAM_RECORDS
 WHERE 1=1
   AND GAME_SEQ      = #{gameSeq}
   AND QUARTER_CODE  = #{quarterCode}

mapper.update:
/* QuarterTeamRecordsRepository.updateQuarterRecords */
UPDATE QUARTER_TEAM_RECORDS
   SET QUARTER_CODE = #{quarterCode}
     , QUARTER_TIME = #{quarterTime}
     , SCORE        = #{score}
     , FREE_THROW   = #{freeThrow}
     , TWO_POINT    = #{twoPoint}
     , THREE_POINT  = #{threePoint}
     , ASSIST       = #{assist}
     , REBOUND      = #{rebound}
     , STEAL        = #{steal}
     , BLOCK        = #{block}
     , TURNOVER     = #{turnover}
     , FOUL         = #{foul}
 WHERE 1=1


   AND QUARTER_TEAM_RECORDS_SEQ = #{quarterTeamRecordsSeq}


   AND GAME_SEQ       = #{gameSeq}
   AND HOME_AWAY_CODE = #{homeAwayCode}
   AND QUARTER_CODE   = #{quarterCode}

mapper.insert:
/* QuarterTeamRecordsRepository.save */

    SELECT LAST_INSERT_ID()

    INSERT INTO
            QUARTER_TEAM_RECORDS
    (
      GAME_SEQ
    , HOME_AWAY_CODE
    , GAME_JOIN_TEAM_SEQ
    , QUARTER_CODE
    , QUARTER_TIME
    , FREE_THROW
    , TWO_POINT
    , THREE_POINT
    , ASSIST
    , REBOUND
    , STEAL
    , BLOCK
    , TURNOVER
    , FOUL
    ) VALUES
    (
      #{gameSeq}
    , #{homeAwayCode}
    , #{gameJoinTeamSeq}
    , #{quarterCode}
    , #{quarterTime}
    , #{freeThrow}
    , #{twoPoint}
    , #{threePoint}
    , #{assist}
    , #{rebound}
    , #{steal}
    , #{block}
    , #{turnover}
    , #{foul}
    )

mapper.delete:
DELETE FROM QUARTER_TEAM_RECORDS
 WHERE 1=1
   AND GAME_SEQ = #{gameSeq}
   AND QUARTER_CODE = #{quarterCode}
