mapper.select:
/* GameJoinManagerRepository.findOpponents */
SELECT T.TEAM_SEQ
     , T.TEAM_NAME
     , T.FOUNDATION_YMD
     , T.HOMETOWN
     , U.NAME  AS LEADER_NAME
  FROM TEAM T
 INNER JOIN USER U
    ON T.LEADER_USER_SEQ = U.USER_SEQ
 WHERE 1=1

   AND T.SIDO_CODE = #{sidoCode}


   AND T.TEAM_NAME = #{teamName}


   AND U.NAME = #{leaderName}
