mapper.select:
/* GameJoinManagerRepository.findOneTeamEntry */ SELECT QPR.QUARTER_PLAYER_RECORDS_SEQ , QPR.GAME_SEQ , QPR.HOME_AWAY_CODE , QPR.GAME_JOIN_TEAM_SEQ , QPR.GAME_JOIN_PLAYER_SEQ , QPR.QUARTER_CODE , QPR.IN_GAME_YN , QPR.TRY_FREE_THROW , QPR.FREE_THROW , QPR.TRY_TWO_POINT , QPR.TWO_POINT , QPR.TRY_THREE_POINT , QPR.THREE_POINT , QPR.ASSIST , QPR.REBOUND , QPR.STEAL , QPR.BLOCK , QPR.TURNOVER , QPR.FOUL , GJP.PLAYER_TYPE_CODE , GJP.NAME , GJP.NAME AS USER_NAME /* 임시처리 TODO NAME 속성과 통일화 필요 */ , GJP.BACK_NUMBER , GJP.POSITION_CODE , GJP.EMAIL FROM GAME_JOIN_PLAYER GJP INNER JOIN QUARTER_PLAYER_RECORDS QPR ON GJP.GAME_JOIN_PLAYER_SEQ = QPR.GAME_JOIN_PLAYER_SEQ WHERE 1=1 AND QPR.QUARTER_CODE = #{quarterCode} AND QPR.IN_GAME_YN = 'Y'
AND GJP.GAME_JOIN_TEAM_SEQ = #{gameJoinTeamSeq}
AND GJP.GAME_SEQ = #{gameSeq} AND GJP.HOME_AWAY_CODE = #{homeAwayCode}

/* GameJoinManagerRepository.findOpponents */ SELECT T.TEAM_SEQ , T.TEAM_NAME , T.FOUNDATION_YMD , T.HOMETOWN , U.NAME AS LEADER_NAME FROM TEAM T INNER JOIN USER U ON T.LEADER_USER_SEQ = U.USER_SEQ WHERE 1=1
AND T.SIDO_CODE = #{sidoCode}
AND T.TEAM_NAME LIKE CONCAT( '%', #{teamName}, '%' )
AND U.NAME LIKE CONCAT( '%', #{leaderName}, '%' )

/* GameJoinManagerRepository.findPaginationGameJoinPlayers */
SELECT *
  FROM (

  ) AS MAIN
 LIMIT #{pagination.rowCount} OFFSET #{pagination.offset}
