mapper.insert:
/* TeamRepository.saveTeam */

    SELECT LAST_INSERT_ID()

INSERT INTO TEAM (
       LEADER_USER_SEQ
     , TEAM_NAME
     , TEAM_IMAGE_PATH
     , HOMETOWN
     , INTRODUCTION
     , FOUNDATION_YMD
     , REG_DATE
     , UPDATE_DATE
     , SIDO_CODE
     , SIGUNGU_CODE
)
VALUES (
       #{leaderUserSeq}
     , #{teamName}
     , #{teamImagePath}
     , #{hometown}
     , #{introduction}
     , #{foundationYmd}
     , #{regDate}
     , #{updateDate}
     , #{sidoCode}
     , #{sigunguCode}
)

mapper.update:
UPDATE TEAM
   SET LEADER_USER_SEQ = #{leaderUserSeq}
     , TEAM_IMAGE_PATH = #{teamImagePath}
     , TEAM_NAME = #{teamName}
     , HOMETOWN = #{hometown}
     , INTRODUCTION = #{introduction}
     , FOUNDATION_YMD = #{foundationYmd}
     , UPDATE_DATE = #{updateDate}
     , SIDO_CODE = #{sidoCode}
     , SIGUNGU_CODE = #{sigunguCode}
 WHERE 1=1
   AND TEAM_SEQ = #{teamSeq}

mapper.delete:
DELETE FROM TEAM
 WHERE 1=1
   AND TEAM_SEQ = #{teamSeq}
