/* TeamInfoRepository.findPaginationTeamInfo */
SELECT *
FROM (

) ORDERS LIMIT #{pagination.rowCount} OFFSET #{pagination.offset}

mapper.sql:
SELECT A.TEAM_SEQ , A.LEADER_USER_SEQ , A.TEAM_NAME , A.TEAM_IMAGE_PATH , A.HOMETOWN , A.INTRODUCTION , A.FOUNDATION_YMD , A.REG_DATE , A.UPDATE_DATE , A.SIDO_CODE , A.SIGUNGU_CODE , ( SELECT COUNT( 1 ) FROM TEAM_MEMBER WHERE 1=1 AND WITHDRAWAL_YN != 'Y' AND TEAM_SEQ = A.TEAM_SEQ GROUP BY TEAM_SEQ ) TOT_MEMBER FROM TEAM A LEFT JOIN TEAM_REGULAR_EXERCISE B ON A.TEAM_SEQ = B.TEAM_SEQ WHERE 1=1
AND A.TEAM_NAME LIKE CONCAT('%', IFNULL(#{teamName}, ''), '%')
AND A.SIGUNGU_CODE = #{sigungu}
AND DAY_OF_THE_WEEK_CODE >= #{startDay} AND DAY_OF_THE_WEEK_CODE <= #{endDay}
AND START_TIME >= #{startTime} AND END_TIME <= #{endTime}
GROUP BY A.TEAM_SEQ ORDER BY A.TEAM_NAME
