mapper.select:
/* UserRepository.findUser */
SELECT

  FROM USER
 WHERE 1=1
   AND USER_SEQ = #{userSeq}

/* UserRepository.findUserByUserId */
SELECT

  FROM USER
 WHERE 1=1
   AND USER_ID = #{userId}

mapper.insert:
/* UserRepository.saveUser */
INSERT INTO
  USER (
       USER_ID
     , NAME
     , PWD
     , BIRTH_YMD
     , EMAIL
     , REG_DATE
     , UPDATE_DATE
     , USER_REG_YN
     , GENDER
     , HEIGHT
     , WEIGHT
     , SIDO_CODE
     , SIGUNGU_CODE
     , POSITION_CODE
     , ROADADDRESS
)
VALUES (
       #{userId}
     , #{name}
     , #{password}
     , #{birthYmd}
     , #{email}
     , #{regDate}
     , #{updateDate}
     , #{userRegYn}
     , #{gender}
     , #{height}
     , #{weight}
     , #{sidoCode}
     , #{sigunguCode}
     , #{positionCode}
     , #{roadAddress})

mapper.update:
/* UserRepository.updateProfile */
UPDATE USER
   SET NAME          = #{name}
     , BIRTH_YMD     = #{birthYmd}
     , GENDER        = #{gender}
     , EMAIL         = #{email}
     , HEIGHT        = #{height}
     , WEIGHT        = #{weight}
     , SIDO_CODE     = #{sidoCode}
     , SIGUNGU_CODE  = #{sigunguCode}
     , POSITION_CODE = #{positionCode}
     , ROADADDRESS   = #{roadAddress}
 WHERE 1=1
   AND USER_SEQ = #{userSeq}

/* UserRepository.updatePassword */
UPDATE USER
   SET PWD      = #{password}
 WHERE 1=1
   AND USER_SEQ = #{userSeq}

mapper.delete:
/* UserRepository.deleteUser */
DELETE FROM USER
 WHERE 1=1
   AND USER_SEQ = #{userSeq}
